"use strict";var onMusicKeyPress=function(e){if(e){var n=e.keyCode,r=document.querySelector('audio[data-key="'+n+'"]'),t=document.querySelector('.key[data-key="'+n+'"]');if(!r)return;r.currentTime=0,r.play(),t.classList.add("playing")}},onTransitionEnded=function(e){"transform"===e.propertyName&&e.target.classList.remove("playing")};window.addEventListener("keydown",onMusicKeyPress);var keys=document.querySelectorAll(".key");keys.forEach(function(e){return e.addEventListener("transitionend",onTransitionEnded)});